// LeaderboardTabs.tsx
import React, { useState, useEffect } from 'react';
import Leaderboard from "./LeaderboardComp";
import SliderComp from "./SliderComp"; // Import the Slider component
import Acc from "./mocks/acc_new.json";

const LeaderboardTabs = () => {
  // State to track the currently selected tab
  const [activeTab, setActiveTab] = useState('tab1');
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [sliderValue, setSliderValue] = useState(50); // Example state for slider

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Function to render the leaderboard based on the selected tab
  const renderLeaderboard = () => {
    switch (activeTab) {
      case 'tab1':
        return <Leaderboard theme={{ base: "light" }} args={Acc} />;
      default:
        return <div>Select a tab</div>;
    }
  };

  return (
    <div className="tabs-container">
      <ul className={`tabs ${isMobile ? 'mobile' : ''}`}>
        {/* Add your tabs here if needed */}
        {/* Example Tab */}
        <li className={activeTab === 'tab1' ? 'is-active' : ''}>
          <a onClick={() => setActiveTab('tab1')}>Tab 1</a>
        </li>
        {/* Add more tabs as needed */}
      </ul>
      <div className="tab-content">
        {renderLeaderboard()}
        {/* Slider Component */}
        <SliderComp
          min={0}
          max={100}
          step={1}
          value={sliderValue}
          onChange={setSliderValue}
        />
        {/* Text Description */}
        <div className="leaderboard-description">
          <p>
            Use the slider above to filter the leaderboard based on your preferred criteria. Adjust the value to see the rankings update in real-time.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LeaderboardTabs;
